function App() {

  // const [busca, setBusca] = useState('');

  // const resultadoFiltro = useMemo(() => {
  //   const lowerBusca = busca.toLowerCase();
  //   return filtrar.filter((filtro) => 
  //   filtrar.toLowerCase().includes(lowerBusca)
  //   );
  // } [busca]);


  return (

    <div className="painel-central">

      <h1><strong>Desafio-React-jr (Buscar Produtos)</strong></h1>

      <form>

        <div className="painel-de-busca">
          <label>Código do produto:</label>
          <input method="post" type=""onChange={''} />

          <label>Descrição:</label>
          <input method="post" onChange={''} />

          <label>Preço:</label>
          <input method="post" onChange={''} />

          <label>Data:</label>
          <input method="post" onChange={''} />
        </div>

        <button className="botaoPesquisar" type="submit" name="pesquisar"method="post" onClick={'lista_todos_os_produtos'}>Pesquisar</button>

        <button className="botaoAlterar" type="submit" name="pesquisar"method="post" onClick={'lista_todos_os_produtos'}>Alterar</button>

        <button className="botaoExcluir" type="submit" name="pesquisar"method="post" onClick={'lista_todos_os_produtos'}>Excluir</button>

      </form>

      <output className="resultadoPesquisa" type="table" method="get" />

    </div>

  );
}

export default App;
