import './App.css';

// import react from React;

function App() {
  return (

    <div className="painel-central">
      <h1><strong>Desafio-React-jr (Buscar Produtos)</strong></h1>
      <form>
        <div className="painel-de-busca">
          <label>Buscar Produtos por código </label>
          <input type="number" method="post" />
          <label>Buscar Produtos por Descrição </label>
          <input type="text" method="post" />
        </div>

      </form>
    </div>
  );
}

export default App;
