import './App.css';
import React, { useState } from 'react';
import Axios from 'axios';
// import ('../react-api/controllers/routes/produtoRoutes.js');
// import produto from '../react-api/controllers/models/produtoControllers.js'

function App() {

  // const [busca, setBusca] = useState('');

  // const resultadoFiltro = useMemo(() => {
  //   const lowerBusca = busca.toLowerCase();
  //   return filtrar.filter((filtro) => 
  //   filtrar.toLowerCase().includes(lowerBusca)
  //   );
  // } [busca]);

  class Produto {

    constructor() {
      this.codigo = 1;
      this.arrayProdutos = [];
    }

    salvar() {
      let produto = this.lerDados();

      if (this.validaCampos(produto)) {
        this.adicionar(produto);
      }
      this.listaTabela();
      this.cancelar();
    }

    listaTabela() {
      let tbody = document.getElementById('tbody');

      for (let i = 0; i < this.arrayProdutos.length; i++) {
        let tr = tbody.insertRow();

        let td_codigo = tr.insertCell();
        let td_descricao = tr.insertCell();
        let td_preco = tr.insertCell();
        let td_data = tr.insertCell();

        td_codigo.innerText = this.arrayProdutos[i].codigo;
        td_descricao.innerText = this.arrayProdutos[i].descricaoProduto;
        td_preco.innerText = this.arrayProdutos[i].preco;
        td_data.classList.add("center")
      }
    }

    adicionar(produto) {
      this.arrayProdutos.push(produto);
      this.codigo++;
    }

    lerDados() {
      let produto = {}

      produto.codigo = this.codigo;
      produto.descricao = document.getElementById('descricao').value;
      produto.preco = document.getElementById('preco').value;

      return produto;
    }

    validaCampos(produto) {
      let msg = '';

      if(produto.descricao == '') {
        msg += "- Informa o Nome do Produto \n";
      }
    }
  }
  return (

    <div className="painel-central">

      <h1><strong>Desafio-React-jr (Buscar Produtos)</strong></h1>

      <form>

        <div className="painel-de-busca">
          <label>Código do produto:</label>
          <input method="post" type="number" id="codigo" onChange={''} />

          <label>Descrição:</label>
          <input method="post" type="text" id="descricao" onChange={''} />

          <label>Preço:</label>
          <input method="post" text="number" id="preco" onChange={''} />

          <label>Data:</label>
          <input method="post" type="date" id="data" onChange={''} />
        </div>

        <button className="botaoPesquisar" type="submit" name="pesquisar" method="post" onClick={"this.listaTabelas()"}>Pesquisar</button>

        <button className="botaoAlterar" type="put" name="pesquisar" method="post" onClick={'lista_todos_os_produtos'}>Alterar</button>

        <button className="botaoExcluir" type="delete" name="pesquisar" method="post" onClick={'lista_todos_os_produtos'}>Excluir</button>

      </form>

      <output className="resultadoPesquisa" type="table" method="get">
        <table border="1">
          <thead>
            <th>Código</th>
            <th>Descrição</th>
            <th>Preço</th>
            <th>Data</th>
          </thead>
          <tbody>
            <tb>{('codigo')}</tb>
            <tb>{('descricao')}</tb>
            <tb>{('preco')}</tb>
            <tb>{('data_cadastro')}</tb>
          </tbody>
        </table>
      </output>

    </div>

  );
}

export default App;
